import { Col, Row } from 'react-bootstrap';
import { Photo } from '../redux/reducer';
import { ChangeEvent, useState } from 'react';

interface Props {
  data: Photo;
}
function MenuItem({ data }: Props) {
  const [value, setValue] = useState<string>(data.title);
  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };
  const id = data.id % 2 === 0;
  return (
    <Row className="mb-4">
      <Col
        md={3}
        className="d-flex justify-content-center"
        style={data.id % 2 === 0 ? { backgroundColor: '#f0f0f0' } : ''}
      >
        <img src={data.thumbnailUrl} style={{ boxShadow: '0px 2px 3px #555' }} width={150} height={150} alt="" />
      </Col>
      <Col md={9}>
        <input
          className="form-control"
          type="text"
          value={value}
          onChange={handleChange}
          style={{ border: 'none', fontSize: '20px', fontWeight: '600' }}
        />
        <p className="mt-3">1231231</p>
      </Col>
    </Row>
  );
}

export default MenuItem;
