import { Col, Row } from 'react-bootstrap';
import ConfirmationDialog from '../../../Component/ConfirmationDialog';
import CustomDialog from '../../../Component/Dialog';
import { Controller } from 'react-hook-form';
interface CreateProductDialogProps {
  open: boolean;
  onClose: () => void;
}
function CreateProductDialog({ open, onClose }: CreateProductDialogProps) {
  return (
    <CustomDialog
      open={true}
      onClose={onClose}
      yes="Lưu"
      no="Hủy"
      onYesClick={() => {}}
      onConfirmDialogClose={onClose}
      textTitle="Tạo sản phẩm mới"
    >
      <Row>
        <Col>
          {' '}
          <label className="form-check-label" htmlFor="invalidCheck">
            Client
          </label>
          <Controller
            name="email"
            defaultValue=""
            control={control}
            rules={{ required: true, pattern: /^\S+@\S+$/i }}
            render={({ field }) => <Form.Control className="mt-2" {...field} isInvalid={!!errors.email} />}
          />
          <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
            {errors.email?.type === 'required' && <FormattedMessage id="require" />}
            {errors.email?.type === 'pattern' && <FormattedMessage id="emailInvalid" />}
          </span>
        </Col>
      </Row>
    </CustomDialog>
  );
}

export default CreateProductDialog;
