import {
  ActionCreator,
  ActionCreatorTypeMetadata,
  TypeConstant,
} from '../type-helpers';

export function checkIsEmpty(arg: unknown) {
  return arg == null;
}

export function throwIsEmpty(argPosition: number): never {
  throw new Error(`Argument ${argPosition} is empty.`);
}

export function checkValidActionCreator(
  arg: unknown
): arg is ActionCreator<TypeConstant> {
  return typeof arg === 'function' && 'getType' in arg;
}

export function checkInvalidActionCreator(arg: unknown) {
  return !checkValidActionCreator(arg);
}

export function throwInvalidActionCreator(argPosition: number): never {
  throw new Error(
    `Argument ${argPosition} is invalid, it should be an action-creator instance from "typesafe-actions"`
  );
}

export function checkInvalidActionCreatorInArray(
  arg: ActionCreator<TypeConstant> & ActionCreatorTypeMetadata<TypeConstant>,
  idx: number
): void | never {
  if (arg == null) {
    throw new Error(
      `Argument contains array with empty element at index ${idx}`
    );
  } else if (arg.getType == null) {
    throw new Error(
      // tslint:disable-next-line:max-line-length
      `Argument contains array with invalid element at index ${idx}, it should be an action-creator instance from "typesafe-actions"`
    );
  }
}

export function checkValidActionType(arg: unknown): arg is string | symbol {
  return typeof arg === 'string' || typeof arg === 'symbol';
}

export function checkInvalidActionType(arg: unknown) {
  return !checkValidActionType(arg);
}

export function throwInvalidActionType(argPosition: number): never {
  throw new Error(
    `Argument ${argPosition} is invalid, it should be an action type of type: string | symbol`
  );
}

export function checkInvalidActionTypeInArray(
  arg: TypeConstant,
  idx: number
): void | never {
  if (arg == null) {
    throw new Error(
      `Argument contains array with empty element at index ${idx}`
    );
  } else if (typeof arg !== 'string' && typeof arg !== 'symbol') {
    throw new Error(
      `Argument contains array with invalid element at index ${idx}, it should be of type: string | symbol`
    );
  }
}

export function throwInvalidActionTypeOrActionCreator(
  argPosition: number
): never {
  throw new Error(
    `Argument ${argPosition} is invalid, it should be an action-creator instance from "typesafe-actions" or action type of type: string | symbol`
  );
}
import { TypeConstant } from './type-helpers';
import {
  checkIsEmpty,
  throwIsEmpty,
  checkInvalidActionType,
  throwInvalidActionCreator,
} from './utils/validation';

export function action<T extends TypeConstant, E>(
  type: T,
  payload: undefined,
  meta: undefined,
  error: E
): { type: T; error: E };

export function action<T extends TypeConstant, M, E>(
  type: T,
  payload: undefined,
  meta: M,
  error: E
): { type: T; meta: M; error: E };

export function action<T extends TypeConstant, P, E>(
  type: T,
  payload: P,
  meta: undefined,
  error: E
): { type: T; payload: P; error: E };

export function action<T extends TypeConstant, P, M, E>(
  type: T,
  payload: P,
  meta: M,
  error: E
): { type: T; payload: P; meta: M; error: E };

export function action<T extends TypeConstant, M>(
  type: T,
  payload: undefined,
  meta: M
): { type: T; meta: M };

export function action<T extends TypeConstant, P, M>(
  type: T,
  payload: P,
  meta: M
): { type: T; payload: P; meta: M };

export function action<T extends TypeConstant, P>(
  type: T,
  payload: P
): { type: T; payload: P };

export function action<T extends TypeConstant>(type: T): { type: T };

/**
 * @description flux standard action factory
 * @example
 * ```
 * const add = (amount: number, meta?: Meta, error?: boolean) => action('INCREMENT', amount, meta, error);
 * ```
 */
export function action<
  T extends TypeConstant,
  P = undefined,
  M = undefined,
  E = undefined
>(type: T, payload?: P, meta?: M, error?: E) {
  if (checkIsEmpty(type)) {
    throwIsEmpty(1);
  }

  if (checkInvalidActionType(type)) {
    throwInvalidActionCreator(1);
  }

  return { type, payload, meta, error } as any;
}
import {
  TypeConstant,
  ResolveType,
  ActionCreatorTypeMetadata,
} from './type-helpers';
import {
  checkIsEmpty,
  throwIsEmpty,
  checkInvalidActionType,
  throwInvalidActionType,
} from './utils/validation';

/**
 * @description create custom action-creator using constructor function with injected type argument
 */
export function createCustomAction<
  TType extends TypeConstant,
  TArgs extends any[] = [],
  TReturn extends any = {}
>(
  type: TType,
  createHandler?: (...args: TArgs) => TReturn
): ((...args: TArgs) => ResolveType<{ type: TType } & TReturn>) &
  ActionCreatorTypeMetadata<TType> {
  if (checkIsEmpty(type)) {
    throwIsEmpty(1);
  }

  if (checkInvalidActionType(type)) {
    throwInvalidActionType(1);
  }

  const actionCreator = (...args: TArgs) => {
    const customProps =
      createHandler != null ? createHandler(...args) : undefined;
    return { type, ...customProps } as ResolveType<{ type: TType } & TReturn>;
  };

  const typeMeta = {
    getType: () => type,
    // redux-actions compatibility
    toString: () => type,
  } as ActionCreatorTypeMetadata<TType>;

  return Object.assign(actionCreator, typeMeta);
}
import {
