import { Col, Row } from 'react-bootstrap';
import './FilterComponent.scss';
import { memo, useState, ChangeEvent } from 'react';
import { useSelector } from 'react-redux';
import { clientSelector, statusSelector } from '../redux/selector';
function FilterComponent() {
  const [formData, setFormData] = useState({
    client: '',
    status: '',
    dateFrom: '',
    dateTo: '',
    invoice: '',
  });
  const clientList = useSelector(clientSelector);
  const statusList = useSelector(statusSelector);
  const handleChangeSelect = (e: ChangeEvent<HTMLSelectElement>) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  const handleChangeInput = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  console.log(formData);
  return (
    <Row>
      <Col md={9}>
        <div className="d-flex justify-content-between">
          <select name="client" onChange={handleChangeSelect} className="input">
            <option value="">Client</option>
            {clientList.map((el, index) => {
              return (
                <option key={index} value={el}>
                  {el}
                </option>
              );
            })}
          </select>
          <select name="status" className="input">
            <option value="">Status</option>
            {statusList.map((el, index) => {
              return (
                <option key={index} value={el}>
                  {el}
                </option>
              );
            })}
          </select>
          <input className="input" onChange={handleChangeInput} name="dateFrom" placeholder="Chọn ngày" type="date" />
          <input className="input " onChange={handleChangeInput} name="dateTo" placeholder="from" type="date" />
          <input className="input" onChange={handleChangeInput} name="invoice" placeholder="#Invoice" type="text" />
        </div>
      </Col>
      <Col md={3}>
        <div className="d-flex ">
          <button className="btn_filter">Apply</button>
          <button className="btn_filter">Clear</button>
        </div>
      </Col>
    </Row>
  );
}

export default memo(FilterComponent);
