import { Col, Row } from 'react-bootstrap';
import { Photo } from '../redux/reducer';
import { ChangeEvent, useState } from 'react';

interface Props {
  data: Photo;
}
function MenuItem({ data }: Props) {
  const [value, setValue] = useState<string>(data.title);
  const [onInput, setOnInput] = useState<boolean>(false);
  const hoverStyle = {
    backgroundColor: '#555',
  };
  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
  };
  return (
    <Row className="mb-4 p-4" style={{ backgroundColor: data.id % 2 === 0 ? '#f0f0f0' : '#fff' }}>
      <Col md={3} className="d-flex justify-content-center">
        <img src={data.thumbnailUrl} style={{ boxShadow: '0px 2px 3px #555' }} width={150} height={150} alt="" />
      </Col>
      <Col md={9}>
        {!onInput && (
          <label
            htmlFor=""
            style={{
              border: 'none',
              fontSize: '20px',
              fontWeight: '600',
              backgroundColor: data.id % 2 === 0 ? '#f0f0f0' : '#fff',
              cursor: 'text',
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.border = hoverStyle.backgroundColor;
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.backgroundColor = hoverStyle.backgroundColor;
            }}
          >
            {value}
          </label>
        )}
        {onInput && (
          <input
            // className="form-control"
            type="text"
            value={value}
            onChange={handleChange}
            style={{
              border: 'none',
              fontSize: '20px',
              fontWeight: '600',
              backgroundColor: data.id % 2 === 0 ? '#f0f0f0' : '#fff',
            }}
          />
        )}

        <p className="mt-3">1231231</p>
      </Col>
    </Row>
  );
}

export default MenuItem;
