import { Button, Col, Form, Row } from 'react-bootstrap';
import ConfirmationDialog from '../../../Component/ConfirmationDialog';
import CustomDialog from '../../../Component/Dialog';
import { Controller, useForm } from 'react-hook-form';
interface CreateProductDialogProps {
  open: boolean;
  onClose: () => void;
}
interface FormData {
  status: string;
  currency: string;
  fundingMethod: string;
  total: number;
  order: string;
  client: string;
  invoice: string;
}
const statusConstant = ['Fulfilled', 'Processing', 'Pending', 'Receicved'];
const color = (data: string) => {
  if (data.includes('Pending')) {
    return '#555';
  } else if (data.includes('Fulfilled')) {
    return '#54b291';
  } else if (data.includes('Processing')) {
    return '#ffdcab';
  } else if (data.includes('Received')) {
    return '#4faece';
  }
};
function CreateProductDialog({ open, onClose }: CreateProductDialogProps) {
  const {
    control,
    handleSubmit,
    formState: { errors },
    watch,
  } = useForm<FormData>();
  const onSubmit = (data: FormData) => {};

  const required = 'Bạn buộc phải nhập trường này';
  return (
    <CustomDialog
      open={true}
      onClose={onClose}
      onConfirmDialogClose={onClose}
      textTitle="Tạo sản phẩm mới"
      size="md"
      maxWidth={true}
    >
      <form onSubmit={handleSubmit(onSubmit)}>
        <Row>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Client
            </label>
            <Controller
              name="client"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.client} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.client?.type === 'required' && <span>{required}</span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Status
            </label>
            <Controller
              name="status"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => (
                <Form.Select className="mt-1" {...field} isInvalid={!!errors.status}>
                  <option value="">--Nhập một lựa chọn--</option>
                  {statusConstant.map((el, index) => {
                    return (
                      <option style={{ color: color(el) }} key={index} value={el}>
                        {el}
                      </option>
                    );
                  })}
                </Form.Select>
              )}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.status?.type === 'required' && <span>{required}</span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Currency
            </label>
            <Controller
              name="currency"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.currency} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.currency?.type === 'required' && <span>{required}</span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Funding method
            </label>
            <Controller
              name="fundingMethod"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.fundingMethod} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.fundingMethod?.type === 'required' && <span>{required}</span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Total
            </label>
            <Controller
              name="total"
              control={control}
              rules={{ required: true }}
              render={({ field }) => (
                <Form.Control className="mt-1" type="number" {...field} isInvalid={!!errors.total} />
              )}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.total?.type === 'required' && <span>{required}</span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Order
            </label>
            <Controller
              name="order"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.order} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.order?.type === 'required' && <span>{required}</span>}
            </span>
          </Col>
          <Col md={12}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Invoice
            </label>
            <Controller
              name="invoice"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.invoice} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.invoice?.type === 'required' && <span>{required}</span>}
            </span>
          </Col>
          <Col md={12} className="d-flex justify-content-between mt-3">
            <div></div>
            <div>
              <Button onClick={onClose} className="btn btn-danger">
                Hủy
              </Button>
              <Button type="submit" className="btn btn-success ms-3">
                Tạo mới
              </Button>
            </div>
          </Col>
        </Row>
      </form>
    </CustomDialog>
  );
}

export default CreateProductDialog;
