import { OFF_LOADING, ON_LOADING } from '../../photo/redux/action';
import { GET_PAYROLL_SUCCESS } from './action';

const intlPayroll = {
  payrollTransactionsList: [],
  loading: false,
};
// eslint-disable-next-line @typescript-eslint/no-redeclare
export interface intlPayroll {
  payrollTransactionsList: Payroll[];
}
export interface Payroll {
  id: number;
  status: string;
  date: string;
  client: string;
  currentcy: string;
  tolal: number;
  invoice: string;
}
interface Action {
  type: string;
  payload?: any;
}

export default function payrollReducer(state: intlPayroll = intlPayroll, action: Action) {
  switch (action.type) {
    case GET_PAYROLL_SUCCESS:
      return { ...state, payrollTransactionsList: action.payload };
    case ON_LOADING:
      return {
        ...state,
        loading: true,
      };
    case OFF_LOADING:
      return {
        ...state,
        loading: false,
      };
    default:
      return state;
  }
}
