import { Controller, useForm } from 'react-hook-form';
import { ILoginParams } from '../../../models/auth';
import { Button, Col, Form, Row } from 'react-bootstrap';
import { FormattedMessage } from 'react-intl';
import { Link } from 'react-router-dom';
import { ROUTES } from '../../../configs/routes';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { getRegion } from '../../../redux/actionSignUp';
interface Props {
  onLogin(values: ILoginParams): void;
  loading: boolean;
  errorMessage: string;
}
interface FormData {
  email: string;
  password: string;
  repeatPassword: string;
  name: string;
  gender: string;
  region: 1;
  state: string;
}

function SignUpForm({ onLogin, loading, errorMessage }: Props) {
  const dispach = useDispatch();
  useEffect(() => {
    dispach(getRegion);
  }, []);
  const info = () => {
    const infoLogin = localStorage.getItem('info-login');
    if (infoLogin) {
      return JSON.parse(infoLogin);
    } else {
      return { email: '', password: '', rememberMe: false };
    }
  };
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm<FormData>();
  const onSubmit = (data: FormData) => {
    onLogin(data);
  };
  const { email, password, rememberMe } = info();
  return (
    <div className="mt-2 mb-5" style={{ maxWidth: '560px', width: '100%' }}>
      {!!errorMessage && (
        <div className="alert alert-danger" role="alert" style={{ width: '100%' }}>
          {errorMessage}
        </div>
      )}
      <form onSubmit={handleSubmit(onSubmit)}>
        <Row>
          <Col md={12}>
            <label className="form-check-label" htmlFor="invalidCheck">
              <FormattedMessage id="email" />
            </label>
            <Controller
              name="email"
              defaultValue={email}
              control={control}
              rules={{ required: true, pattern: /^\S+@\S+$/i }}
