import { Col, Row } from 'react-bootstrap';
import TableItem from './TableItem';
import { useSelector } from 'react-redux';
import { payrollSelector } from '../redux/selector';
import { memo, useState } from 'react';
import ReactPaginate from 'react-paginate';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAnglesLeft, faAnglesRight } from '@fortawesome/free-solid-svg-icons';
function Table() {
  const listPayroll = useSelector(payrollSelector);
  const [currentPage, setCurrentPage] = useState<number>(0);

  const handlePageChange = (selectedPage: { selected: number }) => {
    setCurrentPage(selectedPage.selected);
  };

  const pageCount = Math.ceil(listPayroll.length / 10); // Số lượng trang
  console.log(currentPage);
  const displayItems = listPayroll.slice(currentPage * 10, (currentPage + 1) * 10);
  return (
    <Row className="mt-4">
      <Col md={12}>
        <table className="w-100">
          <thead>
            <tr style={{ color: '#355361', fontSize: '18px', letterSpacing: '1px' }}>
              <th>Status</th>
              <th>Date</th>
              <th>Client</th>
              <th>Currency</th>
              <th>Total</th>
              <th>Invoice</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {displayItems.map((el) => (
              <TableItem key={el.id} data={el} />
            ))}
          </tbody>
        </table>
        <div>
          <ReactPaginate
            previousLabel={<FontAwesomeIcon icon={faAnglesLeft} />}
            nextLabel={<FontAwesomeIcon icon={faAnglesRight} />}
            breakLabel={'...'}
            pageCount={pageCount}
            marginPagesDisplayed={2}
            pageRangeDisplayed={5}
            onPageChange={handlePageChange}
            containerClassName={'pagination'}
            activeClassName={'active'}
          />
        </div>
      </Col>
    </Row>
  );
}

export default memo(Table);
