import { GET_REGION } from './actionSignUp';
import axios from 'axios';
const initialState = {
  region: [],
};
interface Action {
  type: string;
  payload?: any;
}
export interface initSignup {}
const SignUpReducer = (state = initialState, action: Action) => {
  switch (action.type) {
    case GET_REGION:
      axios.get('http://api.training.div3.pgtest.co/api/v1/location').then((res) => {
        return {
          region: res.data.data,
        };
      });
      return state;
    default:
      return state;
  }
};
export default SignUpReducer;
