import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashCan } from '@fortawesome/free-solid-svg-icons';
import { memo } from 'react';
import { Payroll } from '../redux/reducer';
interface PropsTableItem {
  data: Payroll;
}
function TableItem({ data }: PropsTableItem) {
  const color = (data: string) => {
    if (data === 'Pending') {
      return '#555';
    } else if (data === 'Fulfilled') {
      return '#54b291';
    } else if (data === 'Processing') {
      return '#ffdcab';
    } else if (data === 'Received') {
      return '#4faece';
    }
  };
  function formatCurrency(amount: number): string {
    return amount.toLocaleString('en-US', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });
  }
  return (
    <tr className="wrapperTableItem ">
      <td>{data.status}</td>
      <td>03 Sep 2020</td>
      <td>PowerGate</td>
      <td>EUR</td>
      <td style={{ fontWeight: '700' }}>{formatCurrency(2300)}</td>
      <td>14ewr23eqwe_241412Ã dsdfadsfasdfasdfadfadsf</td>
      <td style={{ cursor: 'pointer' }}>
        <FontAwesomeIcon color="#f56a9b" className="ms-4" icon={faTrashCan} />
      </td>
    </tr>
  );
}

export default memo(TableItem);
