import { ThunkAction } from 'redux-thunk';
import { AppState } from '../../../redux/reducer/reducer';
import { getPhotoService } from '../service';
export const GET_PHOTO_SUCCESS = 'GET_PHOTO_SUCCESS';
export const ON_LOADING = 'ON_LOADING';
export const OFF_LOADING = 'OFF_LOADING';

export function getPhoto(id: number): ThunkAction<void, AppState, any, any> {
  return async (dispatch, getState) => {
    try {
      dispatch({ type: ON_LOADING });
      const res = await getPhotoService(id);
      dispatch({ type: OF_LOADING });

      dispatch({ type: GET_PHOTO_SUCCESS, payload: res.data });
    } catch (error) {
      console.log(error);
    }
  };
}
