import { Col, Form, Row } from 'react-bootstrap';
import ConfirmationDialog from '../../../Component/ConfirmationDialog';
import CustomDialog from '../../../Component/Dialog';
import { Controller, useForm } from 'react-hook-form';
interface CreateProductDialogProps {
  open: boolean;
  onClose: () => void;
}
interface FormData {
  status: string;
  currency: string;
  fundingMethod: string;
  total: number;
  order: string;
  client: string;
  invoice: string;
}
function CreateProductDialog({ open, onClose }: CreateProductDialogProps) {
  const {
    control,
    handleSubmit,
    formState: { errors },
    watch,
  } = useForm<FormData>();
  const required = 'Bạn buộc phải nhập trường này';
  const onSubmit = (data: FormData) => {};
  return (
    <CustomDialog
      open={true}
      onClose={onClose}
      yes="Lưu"
      no="Hủy"
      onYesClick={() => {}}
      onConfirmDialogClose={onClose}
      textTitle="Tạo sản phẩm mới"
      size="md"
      maxWidth={true}
    >
      <form onSubmit={handleSubmit(onSubmit)}>
        <Row>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Client
            </label>
            <Controller
              name="client"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.client} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.client?.type === 'required' && <span>{required}</span>}
              {errors.client?.type === 'pattern' && <span></span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Status
            </label>
            <Controller
              name="status"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.status} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.status?.type === 'required' && <span>{required}</span>}
              {errors.status?.type === 'pattern' && <span></span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Currency
            </label>
            <Controller
              name="currency"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.currency} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.currency?.type === 'required' && <span>{required}</span>}
              {errors.currency?.type === 'pattern' && <span></span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Funding method
            </label>
            <Controller
              name="fundingMethod"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.fundingMethod} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.fundingMethod?.type === 'required' && <span>{required}</span>}
              {errors.fundingMethod?.type === 'pattern' && <span></span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Total
            </label>
            <Controller
              name="total"
              control={control}
              rules={{ required: true }}
              render={({ field }) => (
                <Form.Control className="mt-1" type="number" {...field} isInvalid={!!errors.total} />
              )}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.total?.type === 'required' && <span>{required}</span>}
              {errors.total?.type === 'pattern' && <span></span>}
            </span>
          </Col>
          <Col md={6}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Order
            </label>
            <Controller
              name="order"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.order} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.order?.type === 'required' && <span>{required}</span>}
              {errors.order?.type === 'pattern' && <span></span>}
            </span>
          </Col>
          <Col md={12}>
            {' '}
            <label className="form-check-label" htmlFor="invalidCheck">
              Invoice
            </label>
            <Controller
              name="invoice"
              defaultValue=""
              control={control}
              rules={{ required: true }}
              render={({ field }) => <Form.Control className="mt-1" {...field} isInvalid={!!errors.invoice} />}
            />
            <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
              {errors.invoice?.type === 'required' && <span>{required}</span>}
              {errors.invoice?.type === 'pattern' && <span></span>}
            </span>
          </Col>
        </Row>
      </form>
    </CustomDialog>
  );
}

export default CreateProductDialog;
