import { Form } from 'react-bootstrap';
import viMessages from '../../intl/vi.json';
import enMessages from '../../intl/en.json';
import cnMessages from '../../intl/cn.json';
export function getMessages(locale: string): any {
  if (locale.startsWith('en')) {
    return enMessages;
  }
  return viMessages;
}

export default getMessages;
