import { useEffect } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router';
import { getCurrentProduct } from '../redux/action';
import { currentProductSelector } from '../redux/selector';
import { setColorStatus } from '../component/CreateProductDialog';

function DetailProduct() {
  const dispatch = useDispatch();
  const { id } = useParams<any>();
  const currentProduct = useSelector(currentProductSelector);
  console.log(currentProduct);
  useEffect(() => {
    dispatch(getCurrentProduct(+id));
  }, [id]);
  const { client, status, fundingMethod, total, currency, order, invoice } = currentProduct;
  return (
    <Container
      fluid
      style={{
        backgroundImage: 'url(https://wallpaperaccess.com/full/701802.jpg)',
        backgroundSize: 'cover',
        minHeight: '100vh',
      }}
    >
      <Row style={{ minHeight: '100vh' }} className="w-100  d-flex justify-content-center align-items-center">
        <Col
          md={5}
          className="p-4 pt-4 pb-5"
          style={{
            backgroundColor: '#fff',
            borderRadius: '10px',
            boxShadow: '0 2px 5px #888',
            border: '1px solid #ccc',
          }}
        >
          <div className="mt-3 ms-4">
            Client: <span style={{ fontWeight: 700 }}>{client}</span>
          </div>
          <div className="mt-3 ms-4">
            Status: <span style={{ color: setColorStatus(status) }}>{status}</span>
          </div>
          <div className="mt-3 ms-4">
            Currency: <span style={{ fontWeight: 700 }}>{currency}</span>
          </div>
          <div className="mt-3 ms-4">
            Funding method: <span style={{ fontWeight: 700 }}>{fundingMethod}</span>
          </div>
          <div className="mt-3 ms-4">
            Total: <span style={{ fontWeight: 700 }}>{total}</span>
          </div>
          <div className="mt-3 ms-4">
            Order: <span style={{ fontWeight: 700 }}>{order}</span>
          </div>
          <div className="mt-3 ms-4">
            invoice: <span style={{ fontWeight: 700 }}>{invoice}</span>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default DetailProduct;
