import { Action } from '../../../redux/reducer/reducerSignUp';
import { GET_PHOTO_SUCCESS, ON_LOADING } from './action';

export interface IntlPhoto {
  listPhoto: Array<Photo>;
  loading: Boolean;
}
const intlPhoto = { listPhoto: [], loading: false };
export interface Photo {
  albumId: number;
  id: number;
  title: string;
  url: string;
  thumbnailUrl: string;
}

export default function photoReducer(state: IntlPhoto = intlPhoto, action: Action) {
  switch (action.type) {
    case GET_PHOTO_SUCCESS:
      return { ...state, listPhoto: action.payload };
    case ON_LOADING:
      return {
        ...state,
        loading: true,
      };

    default:
      return state;
  }
}
